import { useState } from "react";
import Item from "./Item";

export default function PackingList({ items }) {
  const [sortedBy, setSortedBy] = useState("list");

  let sortedByItems;
  if (sortedBy === "list") return (sortedByItems = items);
  if (sortedBy === "packed")
    return (sortedByItems = items
      .slice()
      .sort(a.packed.localCompare(b.packed)));
  if (sortedBy === "alphabet") return sortedByItems.slice().sort();
  return (
    <div className="list">
      <ul>
        {sortedByItems.map((item) => {
          return <Item item={item} />;
        })}
      </ul>
      <div>
        <select value={sortedBy} onChange={(e) => setSortedBy(e.target.value)}>
          <option value="list">list</option>
          <option value="packed">packed</option>
          <option value="alphabet">alphabet</option>
        </select>
        <button>Clear All List</button>
      </div>
    </div>
  );
}
